record.field:
order_data = self.env['sale.order']._read_group([('x_source_sales_order', 'in', self.ids)], ['x_source_sales_order'], ['__count'])
mapped_data = {order.id: count for order, count in order_data}
for record in self: record['x_x_source_sales_order_sale_order_count'] = mapped_data.get(record.id, 0)

groups = self.env['x_property']._read_group([('x_current_owner', 'in', self.ids)], ['x_current_owner'], ['__count'])
owner_property_counts = {owner.id: count for owner, count in groups}
for record in self: record['x_x_current_owner_x_property_count'] = owner_property_counts.get(record.id, 0)

groups = self.env['x_property']._read_group([('x_condominium', 'in', self.ids)], ['x_condominium'], ['__count'])
condo_counts = {condo.id: count for condo, count in groups}
for record in self: record['x_x_condominium_x_property_count'] = condo_counts.get(record.id, 0)

for record in self:
  #TODOVAVA update these computed fields
    event = record.x_calendar_event_id
    voting_key = event.x_voting_key
    if event.x_attending_ratio > 0 and voting_key:
        x_in_favor = 0
        for attendee_vote in record.x_attendee_vote_ids:
            for attendee in event.attendee_ids:
                if attendee.partner_id.id == attendee_vote.x_attendee.id and attendee_vote.x_vote == 'In Favor': x_in_favor += attendee.x_ratio
        record['x_in_favor'] = x_in_favor / sum([x_ratio.x_ratio for x_ratio in voting_key.x_ratio_ids]) / event.x_attending_ratio
    else: record['x_in_favor'] = 0

for record in self:
    event = record.x_calendar_event_id
    voting_key = event.x_voting_key
    if event.x_attending_ratio > 0 and voting_key:
        x_against = 0
        for attendee_vote in record.x_attendee_vote_ids:
            for attendee in event.attendee_ids:
                if attendee.partner_id.id == attendee_vote.x_attendee.id and attendee_vote.x_vote == 'Against': x_against += attendee.x_ratio
        record['x_against'] = x_against / sum([x_ratio.x_ratio for x_ratio in voting_key.x_ratio_ids]) / event.x_attending_ratio
    else: record['x_against'] = 0

for record in self:
    event = record.x_calendar_event_id
    voting_key = event.x_voting_key
    if event.x_attending_ratio > 0 and voting_key:
        x_abstention = 0
        for attendee_vote in record.x_attendee_vote_ids:
            for attendee in event.attendee_ids:
                if attendee.partner_id.id == attendee_vote.x_attendee.id and attendee_vote.x_vote == 'Abstention': x_abstention += attendee.x_ratio
        record['x_abstention'] = x_abstention / sum([x_ratio.x_ratio for x_ratio in voting_key.x_ratio_ids]) / event.x_attending_ratio
    else: record['x_abstention'] = 0

for record in self:
    criteria = record.x_vote_criteria
    if not criteria or (record.x_in_favor == 0 and record.x_against == 0):
        record['x_outcome'] = False
    else:
        record['x_outcome'] = 'Approved' if record.x_in_favor >= criteria.x_acceptation_threshold else 'Rejected'
