record.field:
if record.x_sale_order_line_id:
    record.x_sale_order_line_id.write({
        'price_unit': record.x_unit_price,
        'technical_price_unit': record.x_unit_price,
        'purchase_price': record.x_unit_cost,
    })
else:
    if record.x_targeted_so:
        new_product = env['product.product'].create({
            'name': record.x_name,
            'default_code': record.x_reference,
            'type': 'service',
            'service_policy': "delivered_manual",
            'sale_ok': True,
            'purchase_ok': True,
            'list_price': record.x_unit_price,
            'standard_price': record.x_unit_cost,
        })
        env['sale.order.line'].create({
            'order_id': record.x_targeted_so.id,
            'product_id': new_product.id,
            'product_uom_id': new_product.uom_id.id,
            'price_unit': record.x_unit_price,
            'technical_price_unit': record.x_unit_price,
            'purchase_price': record.x_unit_cost,
            'x_work_item_ids': [record.id],
        })

if record.x_work_item_able and not record.x_work_item_ids and record.product_id :
    work_item_template = env['x_work_item'].search([('x_product_product_id', '=', record.product_id), ("x_is_template","=",True), ('x_active','=',True)], limit=1)
    work_item_local = work_item_template.copy({
        'x_sale_order_line_id': record.id,
        'x_is_template': False,
    })

if record.x_work_item_ids:
    # Open the local work item
    action = {
        "type": "ir.actions.act_window",
        "res_model": "x_work_item",
        "res_id": record.x_work_item_ids.id,
        "view_mode": "form",
        "target": "new",
    }

for work_item in records:
    if work_item.x_is_template and work_item.x_product_product_id and work_item.x_active:
        if work_item.x_product_product_id.x_tmpl_work_item_id:
            work_item.x_product_product_id.x_tmpl_work_item_id.write({'x_active': False})
        work_item.write({'x_active': True})
        work_item.x_product_product_id['x_tmpl_work_item_id'] = work_item.id
