record.field:
if (taxes := record.taxes_id.filtered('x_is_excise')):
    record['taxes_id'] = [Command.unlink(id) for id in taxes.ids]
if (taxes := record.supplier_taxes_id.filtered('x_is_excise')):
    record['supplier_taxes_id'] = [Command.unlink(id) for id in taxes.ids]
if (categ := record.x_excise_category):
    record.write({'taxes_id': [Command.link(categ.x_sales_tax_id.id)], 'supplier_taxes_id': [Command.link(categ.x_purchase_tax_id.id)]})

no_excises_taxes = env.ref('excise_management.no_excise_tax_sale') | env.ref('excise_management.no_excise_tax_purchase')
for fp in records:
    if fp.x_is_fiscal_deposit:
        no_excises_taxes['fiscal_position_ids'] = [Command.link(fp.id)]
    else:
        no_excises_taxes['fiscal_position_ids'] = [Command.unlink(fp.id)]

all_taxes = env['account.tax'].sudo().search([], order="x_is_excise desc, sequence asc")
for idx, tax in enumerate(all_taxes, start=1):
    tax['sequence'] = idx

for record in records:
    all_moves_ids = env['stock.move']._read_group([
        ('date', '>=', record.x_from_date),
        ('date', '<', record.x_to_date),
        ('x_excise_category', '!=', False), ('x_fiscal_deposit_move', 'not in', ['none', False]), ('state', '=', 'done'),
    ],
    groupby=['x_excise_category', 'x_fiscal_deposit_move'],
    aggregates=['x_excise_quantity:sum', 'x_excise_amount:sum', 'x_packaging_units:sum', 'x_packaging_amount:sum', 'id:recordset']
    )
    record['x_line_ids'] = [
        (5, 0, 0), *[(0, 0, {
            'x_excise_category_id': cat.id, 'x_excise_move_type': m_type, 'x_excise_quantity': qty, 'x_excise_amount': amt, 'x_excise_packaging_units': pkg_unit, 'x_excise_packaging_amount': pkg_amount, 'x_move_ids': [(6, 0, moves.ids)],
        })
        for cat, m_type, qty, amt, pkg_unit, pkg_amount, moves in all_moves_ids ]
    ]
